import React, { useState } from 'react';
import {
    Connection,
    PublicKey,
    clusterApiUrl,
    Keypair,
    Transaction,
    SystemProgram,
} from '@solana/web3.js';

const PROGRAM_ID = process.env.REACT_APP_PROGRAM_ID ? new PublicKey(process.env.REACT_APP_PROGRAM_ID) : null;
const NETWORK = clusterApiUrl('testnet');

function App() {
    const [message, setMessage] = useState('');

    const interactWithBot = async () => {
        if (!PROGRAM_ID) {
            setMessage('PROGRAM_ID is not defined');
            return;
        }

        const connection = new Connection(NETWORK, 'confirmed');
        const payer = Keypair.generate();

        // Airdrop SOL to the payer
        const airdropSignature = await connection.requestAirdrop(
            payer.publicKey,
            LAMPORTS_PER_SOL,
        );

        await connection.confirmTransaction(airdropSignature);

        const transaction = new Transaction().add(
            SystemProgram.transfer({
                fromPubkey: payer.publicKey,
                toPubkey: PROGRAM_ID,
                lamports: 1000,
            })
        );

        const signature = await connection.sendTransaction(transaction, [payer]);

        await connection.confirmTransaction(signature);

        setMessage('Transaction successful with signature: ' + signature);
    };

    return (
        <div className="App">
            <header className="App-header">
                <button onClick={interactWithBot}>Interact with Bot</button>
                <p>{message}</p>
            </header>
        </div>
    );
}

export default App;
